$(document).ready(function() {
  console.log('%c Welcome to FELab, a MullenLowe Profero product. ', 'background: #c3a90a;\ncolor: #ffffff;\nfont-size: 18px;\nfont-family: "Helvetica Neue";\nfont-weight: 300;\nline-height: 30px;\nheight: 30px;\npadding: 5px');
  return $('[data-js-galaxy]').Galaxy();
});

var Galaxy,
  bind = function(fn, me){ return function(){ return fn.apply(me, arguments); }; },
  extend = function(child, parent) { for (var key in parent) { if (hasProp.call(parent, key)) child[key] = parent[key]; } function ctor() { this.constructor = child; } ctor.prototype = parent.prototype; child.prototype = new ctor(); child.__super__ = parent.prototype; return child; },
  hasProp = {}.hasOwnProperty;

Galaxy = (function(superClass) {
  extend(Galaxy, superClass);

  function Galaxy() {
    this.animate = bind(this.animate, this);
    this.shooters = bind(this.shooters, this);
    return Galaxy.__super__.constructor.apply(this, arguments);
  }

  Galaxy.prototype.defaults = {
    size: {
      w: window.innerWidth,
      h: null
    },
    img: {
      lens: MLP.paths.img + 'thumb-lensflare-200x200.png',
      smoke: MLP.paths.img + 'thumb-smoke.png'
    }
  };

  Galaxy.prototype.init = function() {
    Galaxy.__super__.init.call(this);
    this.klass.shooter = 'c-galaxy__shooter';
    this.sel.shooter = {
      l: '.js-galaxy__shooter--l',
      r: '.js-galaxy__shooter--r'
    };
    this.createScene();
    this.render();
    return this.animate();
  };

  Galaxy.prototype.randInt = function(min, max) {
    return ~~((Math.random() * max) + min);
  };

  Galaxy.prototype.getHeight = function() {
    return this.el.target.height() || this.ops.size.h;
  };

  Galaxy.prototype.createScene = function() {
    this.clock = new THREE.Clock();
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(75, this.ops.size.w / this.getHeight(), 0.1, 1000);
    this.renderer = new THREE.WebGLRenderer({
      alpha: true
    });
    this.renderer.setSize(this.ops.size.w, this.getHeight());
    this.renderer.setClearColor(0x001f28, 0);
    return this.el.target.append(this.renderer.domElement);
  };

  Galaxy.prototype.render = function() {
    this.particles();
    return this.shooters();
  };

  Galaxy.prototype.shooters = function() {
    var i, key, shooter, shooters, time;
    shooters = ['l', 'r'];
    i = this.randInt(0, 2);
    key = shooters[i];
    shooter = this.klass.shooter + '--' + shooters[i];
    this.el.target.find(this.sel.shooter[key]).addClass(shooter);
    time = this.randInt(4000, 5000);
    clearTimeout(this.timeHandler);
    return this.timeHandler = setTimeout(((function(_this) {
      return function() {
        _this.el.target.find(_this.sel.shooter[key]).removeClass(shooter);
        return _this.shooters();
      };
    })(this)), time);
  };

  Galaxy.prototype.particles = function() {
    var p, particle, particleMaterial, particleTexture, particles;
    particles = new THREE.Geometry;
    p = 0;
    while (p < 15000) {
      particle = new THREE.Vector3(Math.random() * 500 - 250, Math.random() * 500 - 250, Math.random() * 500 - 250);
      particles.vertices.push(particle);
      p++;
    }
    particleTexture = THREE.ImageUtils.loadTexture(this.ops.img.lens);
    particleMaterial = new THREE.PointCloudMaterial({
      map: particleTexture,
      transparent: true,
      size: .8
    });
    this.particleSystem = new THREE.PointCloud(particles, particleMaterial);
    return this.scene.add(this.particleSystem);
  };

  Galaxy.prototype.animate = function() {
    var delta;
    requestAnimationFrame(this.animate);
    delta = this.clock.getDelta() * .03;
    this.particleSystem.rotation.y += delta;
    return this.renderer.render(this.scene, this.camera);
  };

  return Galaxy;

})(MLP.apps.MLPModule);

$.mlpModule(Galaxy, 'Galaxy');


/*
  Auto formats phone/fax number for mobile.
  Usage:
    a(data-js-intent='tel:0354618432', href='#') 03-5461-8432
    meta(name='format-detection', content='telephone=no')
 */
var Intent,
  extend = function(child, parent) { for (var key in parent) { if (hasProp.call(parent, key)) child[key] = parent[key]; } function ctor() { this.constructor = child; } ctor.prototype = parent.prototype; child.prototype = new ctor(); child.__super__ = parent.prototype; return child; },
  hasProp = {}.hasOwnProperty;

Intent = (function(superClass) {
  extend(Intent, superClass);

  function Intent() {
    return Intent.__super__.constructor.apply(this, arguments);
  }

  Intent.prototype.defaults = {
    screen: {
      mobile: MLP.config.mobile || 767
    }
  };

  Intent.prototype.init = function() {
    Intent.__super__.init.call(this);
    this.events();
    return this.format();
  };

  Intent.prototype.events = function() {
    return $(window).bind('resize orientationchange', (function(_this) {
      return function(e) {
        return _this.format();
      };
    })(this));
  };

  Intent.prototype.contains = function(haystack) {
    return this.intent.indexOf(haystack) > -1;
  };

  Intent.prototype.formatFax = function() {
    var $anchors;
    $anchors = this.el.target.find('a');
    if ($anchors.length) {
      $anchors.addClass('link-fax');
      return $anchors.removeAttr('href');
    }
  };

  Intent.prototype.formatPhone = function() {
    if (this.width <= this.ops.screen.mobile) {
      return this.el.target.attr('href', this.intent);
    } else {
      return this.el.target.removeAttr('href');
    }
  };

  Intent.prototype.format = function() {
    this.width = $(window).width();
    this.intent = this.el.target.data('js-intent');
    if (this.intent) {
      if (this.contains('tel')) {
        this.formatPhone();
      }
      if (this.contains('fax')) {
        return this.formatFax();
      }
    }
  };

  return Intent;

})(MLP.apps.MLPModule);

$.mlpModule(Intent, 'Intent', true);
